SELECT tbl_rot_vendasDetalhadas.Cod_venda, tbl_rot_vendas.Dt_Hr_venda, tbl_cad_prodserv.Cod_ProdServ AS [Cód Prod], [subgrupo_prodserv] & " " & [nome_prodserv] AS [Produto ou Serviço], tbl_rot_vendasDetalhadas.Valor_unitario_vendasDet AS [Valor Vendido], [precovenda_prodserv]-([precovenda_prodserv]*[margemminima_prodserv]) AS [Preço Mínimo], tbl_cad_funcionarios.Nome_funcionario, tbl_cad_funcionarios.mat_funcionario
FROM tbl_cad_funcionarios LEFT JOIN ((tbl_rot_vendasDetalhadas RIGHT JOIN tbl_rot_vendas ON tbl_rot_vendasDetalhadas.Cod_venda = tbl_rot_vendas.Cod_venda) LEFT JOIN tbl_cad_prodserv ON tbl_rot_vendasDetalhadas.Cod_produto = tbl_cad_prodserv.Cod_ProdServ) ON tbl_cad_funcionarios.mat_funcionario = tbl_rot_vendas.Cod_comissionista_G1
WHERE (((IIf([valor_unitario_vendasdet]<[precovenda_prodserv]-([precovenda_prodserv]*[margemminima_prodserv]),"ALERTA","Normal"))="Alerta") AND ((tbl_rot_vendas.AutorizadoPor) Is Null));
