SELECT tbl_detalhesOrdemServico.Cod_OrdemServico, tbl_rot_OrdemServico.DtHr_Abertura_OrdemServico, tbl_cad_funcionarios.Nome_funcionario, [Subgrupo_prodserv] & " " & [Nome_prodserv] AS NomeServ, tbl_rot_OrdemServico.Cod_veiculo
FROM (tbl_cad_prodserv RIGHT JOIN tbl_rot_vendasDetalhadas ON tbl_cad_prodserv.Cod_ProdServ = tbl_rot_vendasDetalhadas.Cod_produto) RIGHT JOIN ((tbl_rot_OrdemServico INNER JOIN (tbl_cad_funcionarios INNER JOIN (tbl_cad_comissionista INNER JOIN tbl_ComissionistaDaOrdemServico ON tbl_cad_comissionista.Cod_comissionista = tbl_ComissionistaDaOrdemServico.Cod_comissionista) ON tbl_cad_funcionarios.mat_funcionario = tbl_cad_comissionista.Mat_funcionario) ON tbl_rot_OrdemServico.Cod_OrdemServico = tbl_ComissionistaDaOrdemServico.cod_OrdemServico) LEFT JOIN tbl_detalhesOrdemServico ON tbl_rot_OrdemServico.Cod_OrdemServico = tbl_detalhesOrdemServico.Cod_OrdemServico) ON tbl_rot_vendasDetalhadas.Cod_VendasDet = tbl_detalhesOrdemServico.Cod_VendasDet;
