SELECT tbl_rot_contasareceber.Cod_contasAreceber, tbl_rot_contasareceber.DtVencimento_contasReceber, tbl_rot_contasareceber.ValorTotal_ContasReceber, tbl_rot_contasareceber.CodVenda_contasReceber, tbl_rot_contasareceber.PlanoMensal, Month([UltimaVerificacaoPM]) AS UltimaVerificacaoPME, tbl_rot_contasareceber.ContasReceber_Ativo, CDate(DateSerial(Year(Date()),Month(Date()),Day([DtVencimento_contasReceber]))-Nz(CInt(DLookUp("[NomeAmigavel]","tbl_cad_clientesFLM","[CodCliente_FLM]=" & 13)),0)) AS DtCobraParcela, CDate(DateSerial(Year(Date()),Month(Date()),Day([DtVencimento_contasReceber]))-Nz(CInt(DLookUp("[NomeAmigavel]","tbl_cad_clientesFLM","[CodCliente_FLM]=" & 14)),0)) AS Dt1lembreteParcela
FROM tbl_rot_contasareceber
WHERE (((tbl_rot_contasareceber.PlanoMensal)=Yes) AND ((Month([UltimaVerificacaoPM])) Is Null Or (Month([UltimaVerificacaoPM]))<Month(Date())) AND ((tbl_rot_contasareceber.ContasReceber_Ativo)=Yes));
