SELECT tbl_cad_funcionarios.mat_funcionario, tbl_cad_funcionarios.Nome_funcionario, [cs_RegPonto1]![Data] AS 1Data, cs_RegPonto1.Hora AS 1Hora, [cs_RegPonto2]![Data] AS 2Data, cs_RegPonto2.Hora AS 2Hora, [cs_RegPonto3]![Data] AS 3Data, cs_RegPonto3.Hora AS 3Hora, [cs_RegPonto4]![Data] AS 4Data, cs_RegPonto4.Hora AS 4Hora, [cs_RegPonto2]![Data]-[cs_RegPonto1]![Data] AS Atraso1, Nz([cs_RegPonto4]![Data]-[cs_RegPonto3]![Data],"INCOMPLETO") AS Atraso2
FROM (((cs_RegPonto1 RIGHT JOIN tbl_cad_funcionarios ON cs_RegPonto1.mat_funcionario = tbl_cad_funcionarios.mat_funcionario) LEFT JOIN cs_RegPonto2 ON tbl_cad_funcionarios.mat_funcionario = cs_RegPonto2.mat_funcionario) LEFT JOIN cs_RegPonto3 ON tbl_cad_funcionarios.mat_funcionario = cs_RegPonto3.mat_funcionario) LEFT JOIN cs_RegPonto4 ON tbl_cad_funcionarios.mat_funcionario = cs_RegPonto4.mat_funcionario;
