SELECT tbl_rot_vendas.Cod_venda, tbl_rot_vendas.Dt_Hr_venda, [NomeRazaoSocial_Cliente] & " " & [telefone_cliente] AS Cliente, tbl_cad_formasPagamento.Nome_FormaPagamento, tbl_cad_formasPagamentoVENDASDET.QtdVezes_venda, tbl_cad_formasPagamentoVENDASDET.Valor_FormaPagVendasDet, tbl_cad_formasPagamento.Cod_formasPagamento
FROM (((tbl_rot_vendas INNER JOIN tbl_cad_formasPagamentoVENDASDET ON tbl_rot_vendas.Cod_venda = tbl_cad_formasPagamentoVENDASDET.Cod_Venda) LEFT JOIN tbl_cad_clientes ON tbl_rot_vendas.Cod_cliente = tbl_cad_clientes.Cod_cliente) LEFT JOIN tbl_cad_Metas ON tbl_rot_vendas.Dt_Hr_venda = tbl_cad_Metas.Data_Metas) LEFT JOIN tbl_cad_formasPagamento ON tbl_cad_formasPagamentoVENDASDET.Cod_formasPagamento = tbl_cad_formasPagamento.Cod_formasPagamento
WHERE (((tbl_rot_vendas.Cod_formaRetiradaEstoque)=1) AND ((tbl_rot_vendas.VendaFinalizada_vendas)=Yes));
