SELECT tbl_data.data AS Dt_Hr_vend, CCur([Entrada]) AS [Valor Entrada], CCur([Cs_DespesaPorPeriodo_completo].[ValorSaída]) AS [Valor Saída], [Cs_ReceitaPorPeriodo_total_Completo]![Entrada]-[Cs_DespesaPorPeriodo_completo]![ValorSaída] AS Saldo, CCur([Cs_ReceitaPorPeriodo_total_Completo]![Entrada])+CCur([Cs_DespesaPorPeriodo_completo]![ValorSaída]) AS TesteMov
FROM (tbl_data LEFT JOIN Cs_ReceitaPorPeriodo_total_Completo ON tbl_data.Data = Cs_ReceitaPorPeriodo_total_Completo.Data) LEFT JOIN Cs_DespesaPorPeriodo_completo ON tbl_data.Data = Cs_DespesaPorPeriodo_completo.Data
WHERE (((tbl_data.data) Between [Formulários]![Frm_totalporvendaporperiodo]![TxtDtInicial] And [Formulários]![Frm_totalporvendaporperiodo]![TxtDtFinal]+1) AND ((CCur([Cs_ReceitaPorPeriodo_total_Completo]![Entrada])+CCur([Cs_DespesaPorPeriodo_completo]![ValorSaída]))<>0));
