SELECT tbl_rot_contasareceber.Cod_contasAreceber, tbl_rot_contasareceber.CodCliente_contasReceber, tbl_rot_ContasReceber_det.cod_ContasReceberDET, tbl_rot_PagCli_contasReceber.DataPag_ContasReceberDET, tbl_rot_PagCli_contasReceber.ValorPagoPagCli_CR, tbl_rot_PagCli_contasReceber.JurosPagCli_CR, tbl_rot_PagCli_contasReceber.DescontoPagCli_CR, tbl_cad_formasPagamento.Nome_FormaPagamento
FROM ((tbl_rot_contasareceber LEFT JOIN tbl_rot_ContasReceber_det ON tbl_rot_contasareceber.Cod_contasAreceber = tbl_rot_ContasReceber_det.Cod_ContasReceber) LEFT JOIN tbl_rot_PagCli_contasReceber ON tbl_rot_ContasReceber_det.cod_ContasReceberDET = tbl_rot_PagCli_contasReceber.codContasReceberDET) LEFT JOIN tbl_cad_formasPagamento ON tbl_rot_PagCli_contasReceber.FormaPagPagCli_CR = tbl_cad_formasPagamento.Cod_formasPagamento;
