SELECT DateSerial(Year([dtmovimento_lanccaixa]),Month([dtmovimento_lanccaixa]),Day([dtmovimento_lanccaixa])) AS Dt_Hr_vend, Sum(tbl_rot_LancCaixa.Valor_LancCaixa) AS [Valor Entrada], Sum(0) AS [Valor Saída], "Receita" AS Tipo
FROM (tbl_rot_LancCaixa INNER JOIN TMP_Planocontas ON tbl_rot_LancCaixa.PlanoConta_LancCaixa = TMP_Planocontas.codAut_PlanoContas) INNER JOIN TMP_Planocontas_Entrada ON tbl_rot_LancCaixa.PlanoConta_LancCaixa = TMP_Planocontas_Entrada.codAut_PlanoContas
GROUP BY DateSerial(Year([dtmovimento_lanccaixa]),Month([dtmovimento_lanccaixa]),Day([dtmovimento_lanccaixa])), "Receita", tbl_rot_LancCaixa.Tipo_LancCaixa, TMP_Planocontas_Entrada.Ativo_PlanoContas
HAVING (((DateSerial(Year([dtmovimento_lanccaixa]),Month([dtmovimento_lanccaixa]),Day([dtmovimento_lanccaixa]))) Between [Formulários]![Frm_totalporvendaporperiodo]![TxtDtInicial] And [Formulários]![Frm_totalporvendaporperiodo]![TxtDtFinal]+1) AND ((tbl_rot_LancCaixa.Tipo_LancCaixa)="crédito") AND ((TMP_Planocontas_Entrada.Ativo_PlanoContas)=Yes));
