SELECT tbl_rot_MovEstoque.CodVenda_RetiradaEst, tbl_rot_LancCaixa.Cod_LancCaixa, tbl_rot_LancCaixa.DtMovimento_LancCaixa, tbl_rot_LancCaixa.Tipo_LancCaixa, tbl_rot_LancCaixa.Valor_LancCaixa, tbl_cad_formasPagamento.Nome_FormaPagamento
