SELECT tbl_rot_vendas.Dt_Hr_venda, [subgrupo_prodserv] & " " & [nome_prodserv] AS [Produto ou Serviço], tbl_rot_vendasDetalhadas.Valor_unitario_vendasDet, tbl_rot_vendasDetalhadas.Quantidade_VendasDet, [valor_unitario_vendasdet]*[quantidade_vendasdet] AS Total, tbl_cad_grupo_comissionista.ComissaoFixa_procent_grupoComissionista, ([valor_unitario_vendasdet]*[quantidade_vendasdet])*[ComissaoFixa_procent_grupoComissionista] AS [Total da Comissao], tbl_cad_funcionarios.Nome_funcionario, tbl_cad_grupo_comissionista.Grupo_comissionista, tbl_cad_comissionista.Mat_funcionario
FROM (((tbl_cad_prodserv LEFT JOIN tbl_rot_vendasDetalhadas ON tbl_cad_prodserv.Cod_ProdServ = tbl_rot_vendasDetalhadas.Cod_produto) LEFT JOIN tbl_rot_vendas ON tbl_rot_vendasDetalhadas.Cod_venda = tbl_rot_vendas.Cod_venda) LEFT JOIN (tbl_cad_funcionarios LEFT JOIN tbl_cad_comissionista ON tbl_cad_funcionarios.mat_funcionario = tbl_cad_comissionista.Mat_funcionario) ON tbl_rot_vendas.Cod_comissionista_G1 = tbl_cad_funcionarios.mat_funcionario) LEFT JOIN tbl_cad_grupo_comissionista ON tbl_cad_comissionista.Grupo_comissionista = tbl_cad_grupo_comissionista.Cod_Grupo_comissionista
WHERE (((tbl_cad_comissionista.Mat_funcionario)=[Formulários]![Frm_MenuPrinc]![MatFunc]) AND ((tbl_cad_prodserv.PermitirComissao_prodserv)=Yes) AND ((tbl_rot_vendas.VendaFinalizada_vendas)=Yes));
