SELECT Cs_Resumo_HierarquiaGrupos.login_usuarioSistema, Cs_Resumo_HierarquiaGrupos.Grupo_grupoUsuario, Cs_Resumo_HierarquiaGrupos.Cod_GrupoSistemaABAIXO, tbl_cad_alertas.NomeAlerta, tbl_cad_formHiperlink.Cod_CadForm, tbl_cad_alertas.NumTela_Alerta
FROM ((Cs_Resumo_HierarquiaGrupos LEFT JOIN tbl_cad_Alertas_Permissao ON Cs_Resumo_HierarquiaGrupos.Cod_GrupoSistemaABAIXO = tbl_cad_Alertas_Permissao.Cod_grupoUsuariosSistema_alerta) LEFT JOIN tbl_cad_alertas ON tbl_cad_Alertas_Permissao.Cod_Alertas_Permissao = tbl_cad_alertas.Cod_Alertas) LEFT JOIN tbl_cad_formHiperlink ON tbl_cad_alertas.Cod_FormRel_alerta = tbl_cad_formHiperlink.Cod_CadForm
GROUP BY Cs_Resumo_HierarquiaGrupos.login_usuarioSistema, Cs_Resumo_HierarquiaGrupos.Grupo_grupoUsuario, Cs_Resumo_HierarquiaGrupos.Cod_GrupoSistemaABAIXO, tbl_cad_alertas.NomeAlerta, tbl_cad_formHiperlink.Cod_CadForm, tbl_cad_alertas.NumTela_Alerta, tbl_cad_alertas.NumTela_Alerta
HAVING (((Cs_Resumo_HierarquiaGrupos.login_usuarioSistema)=[Formul√°rios]![Frm_MenuPrinc]![txtUsuarioLogado]));
