SELECT tbl_rot_MovEstoque.CodRetiradaEst, tbl_rot_MovEstoque.dtHrRetiradaEst, tbl_rot_MovEstoque.FormaRetiradaEst, tbl_rot_MovEstoque.StatusMovEstoque, tbl_rot_MovEstoque_Prod.CodProd_PME_ENTRADA, tbl_cad_prodserv.Nome_prodServ, tbl_NrSerie_Prod.NumeroSerieProd, tbl_rot_MovEstoque_Prod.dtEnvioAoFabricante, tbl_rot_MovEstoque_Prod.DtRetornoFabricante, tbl_rot_MovEstoque_Prod.liberado_estoque, tbl_rot_MovEstoque_Prod.Observacao_PME, tbl_rot_MovEstoque.CodVenda_RetiradaEst, tbl_cad_prodserv.ProdutoAtivo_prodServ
FROM ((tbl_rot_MovEstoque LEFT JOIN tbl_rot_MovEstoque_Prod ON tbl_rot_MovEstoque.CodRetiradaEst = tbl_rot_MovEstoque_Prod.CodMovEstoque) LEFT JOIN tbl_cad_prodserv ON tbl_rot_MovEstoque_Prod.CodProd_PME_ENTRADA = tbl_cad_prodserv.Cod_ProdServ) LEFT JOIN tbl_NrSerie_Prod ON tbl_rot_MovEstoque_Prod.NS_Prod_PME_ENTRADA = tbl_NrSerie_Prod.cod_NrSerie
WHERE (((tbl_rot_MovEstoque.FormaRetiradaEst)=5) AND ((tbl_rot_MovEstoque.CodVenda_RetiradaEst) Is Not Null) AND ((tbl_cad_prodserv.ProdutoAtivo_prodServ)=Yes));
