SELECT tbl_cad_GrupoSistema.cod_grupoUsuario, tbl_cad_GrupoSistema.Grupo_grupoUsuario, tbl_cad_hierarquiaGruposSistema.cod_hierarquia, tbl_cad_hierarquiaGruposSistema.Cod_GrupoSistemaABAIXO, tbl_cad_UsuarioSistema.login_usuarioSistema
FROM (tbl_cad_GrupoSistema LEFT JOIN tbl_cad_UsuarioSistema ON tbl_cad_GrupoSistema.cod_grupoUsuario = tbl_cad_UsuarioSistema.Cod_Grupo_usuarioSistema) LEFT JOIN tbl_cad_hierarquiaGruposSistema ON tbl_cad_GrupoSistema.cod_grupoUsuario = tbl_cad_hierarquiaGruposSistema.Cod_GrupoSistemaACIMA;
