SELECT tbl_rot_vendasDetalhadas.Cod_venda, tbl_rot_vendasDetalhadas.Cod_produto, tbl_rot_vendasDetalhadas.Quantidade_VendasDet, tbl_rot_vendasDetalhadas.Valor_unitario_vendasDet, tbl_rot_vendas.Dt_Hr_venda, tbl_rot_vendas.Cod_cliente, tbl_rot_vendas.VendaFinalizada_vendas, tbl_rot_vendas.OperadorCaixa_vendas, tbl_cad_funcionarios.Nome_funcionario AS Vendedor
FROM (tbl_cad_funcionarios INNER JOIN (tbl_cad_comissionista INNER JOIN tbl_rot_vendas ON tbl_cad_comissionista.Cod_comissionista = tbl_rot_vendas.Cod_comissionista_G1) ON tbl_cad_funcionarios.mat_funcionario = tbl_cad_comissionista.Mat_funcionario) INNER JOIN tbl_rot_vendasDetalhadas ON tbl_rot_vendas.Cod_venda = tbl_rot_vendasDetalhadas.Cod_venda;
