SELECT tbl_rot_LancCaixa.Cod_LancCaixa AS Cod, tbl_rot_LancCaixa.DtMovimento_LancCaixa, cst_LancCaixa_Credito.Valorcredito AS ValorCredito, cst_LancCaixa_Debito.Valordebito AS ValorDebito, tbl_rot_LancCaixa.Descricao_LancCaixa, tbl_rot_LancCaixa.PlanoConta_LancCaixa, tbl_cad_Planocontas.Nome_PlanoContas, tbl_rot_LancCaixa.NumDoc_LancCaixa, Nz(DSum("[valorCredito]-[valorDebito]","cs_todoslanccaixa","cod <=" & [cod]),[valorCredito]-[valorDebito]) AS Saldo
FROM ((cst_LancCaixa_Credito RIGHT JOIN tbl_rot_LancCaixa ON cst_LancCaixa_Credito.Cod_LancCaixa = tbl_rot_LancCaixa.Cod_LancCaixa) LEFT JOIN cst_LancCaixa_Debito ON tbl_rot_LancCaixa.Cod_LancCaixa = cst_LancCaixa_Debito.Cod_LancCaixa) LEFT JOIN tbl_cad_Planocontas ON tbl_rot_LancCaixa.PlanoConta_LancCaixa = tbl_cad_Planocontas.codAut_PlanoContas;
