INSERT INTO TMP_ContasReceber_Saldo_Individual ( ValorPago, Juros, Desconto, TotalValorPago, DataPag_ContasReceberDET, Nome_FormaPagamento, DescricaoPagCli_CR, usuario )
SELECT Sum(Nz([ValorPagoPagCli_CR],0)) AS ValorPago, Sum(Nz([JurosPagCli_CR],0)) AS Juros, Sum(Nz([DescontoPagCli_CR],0)) AS Desconto, Sum(Nz((([ValorPagoPagCli_CR]+[JurosPagCli_CR])-[DescontoPagCli_CR]),0)) AS TotalValorPago, tbl_rot_PagCli_contasReceber.DataPag_ContasReceberDET, tbl_cad_formasPagamento.Nome_FormaPagamento, tbl_rot_PagCli_contasReceber.DescricaoPagCli_CR, tbl_cad_UsuarioSistema.login_usuarioSistema
FROM (tbl_rot_PagCli_contasReceber LEFT JOIN tbl_cad_formasPagamento ON tbl_rot_PagCli_contasReceber.FormaPagPagCli_CR = tbl_cad_formasPagamento.Cod_formasPagamento) LEFT JOIN tbl_cad_UsuarioSistema ON tbl_rot_PagCli_contasReceber.MatFunc = tbl_cad_UsuarioSistema.matFuncionario
GROUP BY tbl_rot_PagCli_contasReceber.DataPag_ContasReceberDET, tbl_cad_formasPagamento.Nome_FormaPagamento, tbl_rot_PagCli_contasReceber.DescricaoPagCli_CR, tbl_cad_UsuarioSistema.login_usuarioSistema
HAVING (((tbl_rot_PagCli_contasReceber.DataPag_ContasReceberDET)=[Formul√°rios]![frm_saldocaixa_individual]![txt_data]));
