SELECT tbl_rot_contasareceber.CodCliente_contasReceber, tbl_rot_contasareceber.Cod_contasAreceber, tbl_rot_contasareceber.CodVenda_contasReceber, tbl_rot_contasareceber.Cod_contasAreceber, tbl_rot_ContasReceber_det.NumeracaoParc_ContasReceberDET AS [Nr Parc], tbl_rot_ContasReceber_det.DataVencimento_contasReceberDet, tbl_rot_ContasReceber_det.ValorParc_ContasReceberDET, Cs_DebitoPagCli_PAGO.TotalValorPago, tbl_rot_ContasReceber_det.Descricao_ContasReceberDET AS Descrição, tbl_rot_ContasReceber_det.cod_ContasReceberDET, tbl_rot_vendas.VendaFinalizada_vendas
FROM ((tbl_rot_contasareceber LEFT JOIN tbl_rot_ContasReceber_det ON tbl_rot_contasareceber.Cod_contasAreceber = tbl_rot_ContasReceber_det.Cod_ContasReceber) LEFT JOIN Cs_DebitoPagCli_PAGO ON tbl_rot_ContasReceber_det.cod_ContasReceberDET = Cs_DebitoPagCli_PAGO.cod_ContasReceberDET) LEFT JOIN tbl_rot_vendas ON tbl_rot_contasareceber.CodVenda_contasReceber = tbl_rot_vendas.Cod_venda
WHERE (((tbl_rot_ContasReceber_det.NumeracaoParc_ContasReceberDET) Is Not Null) AND ((tbl_rot_vendas.VendaFinalizada_vendas) Is Null Or (tbl_rot_vendas.VendaFinalizada_vendas)=Yes));
