SELECT tbl_cad_prodserv.Cod_ProdServ, tbl_cad_prodserv.Ref_prodserv, tbl_cad_prodserv.Grupo_prodServ, tbl_cad_prodserv.SubGrupo_prodserv, tbl_cad_prodserv.Nome_prodServ, tbl_cad_prodserv.Unidade_prodserv, tbl_cad_prodserv.Tempo_atendServ_prodServ, tbl_cad_prodserv.precoVenda_prodserv, tbl_cad_prodserv.Permissao_Venda_prodServ, tbl_cad_prodserv.ProdutoAtivo_prodServ, tbl_cad_prodserv.Fornecedor_prodServ, tbl_cad_prodserv.EstoqueMinimo_prodServ, tbl_cad_prodserv.Observacao_prodServ, tbl_cad_prodserv.Anexo_prodServ, tbl_cad_prodserv.custo_final_prodserv, tbl_cad_prodserv.precoVenda_prodserv, IIf([Total em estoque]<0,0,[custo_final_prodserv]*[total em estoque]) AS TotalEmPrecoCusto, IIf([Total em estoque]<0,0,[precovenda_prodserv]*[total em estoque]) AS TotalAvender, [totalavender]-[totalemprecocusto] AS LucroTotal, Cs_Estoque.[Total em estoque]
FROM tbl_cad_prodserv LEFT JOIN Cs_Estoque ON tbl_cad_prodserv.Cod_ProdServ = Cs_Estoque.Cod_ProdServ
WHERE (((tbl_cad_prodserv.ProdutoAtivo_prodServ)=Yes));
