SELECT tbl_cad_Planocontas.Categoria_PlanoContas, tbl_cad_Planocontas.Nome_PlanoContas, Sum(tbl_rot_PagFornecedor_contasApagar.ValorPagoPagForn_CP) AS SomaDeValorPagoPagForn_CP, tbl_cad_formasPagContasaPagar.Nome_formaPagContasaPagar
FROM (((tbl_rot_contaApagar LEFT JOIN tbl_rot_contaApagar_Det ON tbl_rot_contaApagar.cod_contaaPagar = tbl_rot_contaApagar_Det.cod_contaaPagar_CADET) LEFT JOIN tbl_rot_PagFornecedor_contasApagar ON tbl_rot_contaApagar_Det.Cod_CADET = tbl_rot_PagFornecedor_contasApagar.codContasApagarDET) LEFT JOIN tbl_cad_Planocontas ON tbl_rot_contaApagar.PlanoPagamento_contasaPagar = tbl_cad_Planocontas.codAut_PlanoContas) LEFT JOIN tbl_cad_formasPagContasaPagar ON tbl_rot_PagFornecedor_contasApagar.FormaPagPagForn_CP = tbl_cad_formasPagContasaPagar.cod_formaPagContasaPagar
GROUP BY tbl_cad_Planocontas.Categoria_PlanoContas, tbl_cad_Planocontas.Nome_PlanoContas, tbl_cad_formasPagContasaPagar.Nome_formaPagContasaPagar, tbl_rot_PagFornecedor_contasApagar.DataPag_ContasApagarDET
HAVING (((Sum(tbl_rot_PagFornecedor_contasApagar.ValorPagoPagForn_CP))>0) AND ((tbl_rot_PagFornecedor_contasApagar.DataPag_ContasApagarDET)=[Formul√°rios]![frm_saldocaixa_individual]![txt_data]));
