SELECT tbl_rot_vendasDetalhadas.Cod_VendasDet, tbl_rot_vendasDetalhadas.Cod_venda, tbl_cad_prodserv.Cod_ProdServ, [cod_prodserv] & " " & [Nome_prodServ] AS Produto, tbl_rot_vendasDetalhadas.Quantidade_VendasDet, tbl_cad_prodserv.precoVenda_prodserv, tbl_cad_prodserv.MargemMinima_prodServ, tbl_rot_vendasDetalhadas.Valor_unitario_vendasDet, tbl_cad_prodserv.custo_final_prodserv, ([Valor_unitario_vendasDet]/[custo_final_prodserv])-1 AS MargemLucro, [valor_unitario_vendasdet]*[quantidade_vendasDet] AS ValorTotal_porproduto, [custo_final_prodserv]+([custo_final_prodserv]*[margemminima_prodserv]) AS ValorMinimo, tbl_rot_vendasDetalhadas.Largura_prodserv, tbl_rot_vendasDetalhadas.Altura_prodserv, tbl_rot_vendasDetalhadas.cod_grupoProdServ, tbl_cad_prodserv.VendaAbaixoEstoque_prodserv, tbl_cad_prodserv.Localizacao_prodserv, tbl_cad_prodserv.Grupo_prodServ, tbl_cad_prodserv.SubGrupo_prodserv, tbl_cad_prodserv.TipoCalculo_prodserv
FROM (tbl_cad_prodserv RIGHT JOIN tbl_rot_vendasDetalhadas ON tbl_cad_prodserv.Cod_ProdServ = tbl_rot_vendasDetalhadas.Cod_produto) LEFT JOIN (tbl_Prodserv_Promocao LEFT JOIN tbl_ProdServ_promDET ON tbl_Prodserv_Promocao.CodPromocao = tbl_ProdServ_promDET.CodPromocao) ON tbl_rot_vendasDetalhadas.Cod_produto = tbl_Prodserv_Promocao.CodPromocao
ORDER BY tbl_rot_vendasDetalhadas.Cod_VendasDet;
