SELECT tbl_rot_MovEstoque.CodVenda_RetiradaEst AS CodVenda, tbl_rot_MovEstoque.dtHrRetiradaEst, tbl_cad_clientes.NomeRazaoSocial_Cliente AS Cliente, tbl_cad_forma_retiradaEstoque.forma_retiradaEstoque AS Tipo
FROM (((tbl_rot_MovEstoque LEFT JOIN tbl_cad_forma_retiradaEstoque ON tbl_rot_MovEstoque.FormaRetiradaEst = tbl_cad_forma_retiradaEstoque.cod_retiradaEstoque) LEFT JOIN tbl_cad_StatusMovEstoque ON tbl_rot_MovEstoque.StatusMovEstoque = tbl_cad_StatusMovEstoque.codStatusMovEstoque) LEFT JOIN tbl_cad_funcionarios ON tbl_rot_MovEstoque.AutorizadoPor_RetiradaEst = tbl_cad_funcionarios.mat_funcionario) LEFT JOIN (tbl_rot_vendas LEFT JOIN tbl_cad_clientes ON tbl_rot_vendas.Cod_cliente = tbl_cad_clientes.Cod_cliente) ON tbl_rot_MovEstoque.CodVenda_RetiradaEst = tbl_rot_vendas.Cod_venda;
