SELECT tbl_rot_MovEstoque_Prod.CodMovEstoque AS CodMov, tbl_rot_MovEstoque.CodVenda_RetiradaEst AS CodVenda, tbl_rot_MovEstoque_Prod.CodProd_PME_ENTRADA AS [CodProd/IN], tbl_NrSerie_Prod.NumeroSerieProd AS [N/S IN], tbl_rot_MovEstoque_Prod.CodProd_PME_SAIDA AS [CodProd/OUT], tbl_NrSerie_Prod_1.NumeroSerieProd AS [N/S OUT], tbl_cad_forma_retiradaEstoque.forma_retiradaEstoque AS Tipo, tbl_rot_MovEstoque_Prod.liberado_estoque AS Liberar
FROM (tbl_rot_MovEstoque LEFT JOIN ((tbl_rot_MovEstoque_Prod LEFT JOIN tbl_NrSerie_Prod ON tbl_rot_MovEstoque_Prod.CodProd_PME_ENTRADA = tbl_NrSerie_Prod.CodProdServ_Nrserie) LEFT JOIN tbl_NrSerie_Prod AS tbl_NrSerie_Prod_1 ON tbl_rot_MovEstoque_Prod.CodProd_PME_SAIDA = tbl_NrSerie_Prod_1.CodProdServ_Nrserie) ON tbl_rot_MovEstoque.CodRetiradaEst = tbl_rot_MovEstoque_Prod.CodMovEstoque) LEFT JOIN tbl_cad_forma_retiradaEstoque ON tbl_rot_MovEstoque.FormaRetiradaEst = tbl_cad_forma_retiradaEstoque.cod_retiradaEstoque;
