SELECT tbl_rot_LancCaixa.Cod_LancCaixa, cst_LancCaixa_Credito.Valorcredito AS ValorCredito, cst_LancCaixa_Debito.Valordebito AS ValorDebito, tbl_rot_LancCaixa.Descricao_LancCaixa, tbl_rot_LancCaixa.PlanoConta_LancCaixa, tbl_rot_LancCaixa.usuariologado_lanccaixa, tbl_rot_LancCaixa.DtMovimento_LancCaixa, tbl_cad_formasPagamento.Nome_FormaPagamento
FROM ((cst_LancCaixa_Credito RIGHT JOIN tbl_rot_LancCaixa ON cst_LancCaixa_Credito.Cod_LancCaixa = tbl_rot_LancCaixa.Cod_LancCaixa) LEFT JOIN cst_LancCaixa_Debito ON tbl_rot_LancCaixa.Cod_LancCaixa = cst_LancCaixa_Debito.Cod_LancCaixa) LEFT JOIN tbl_cad_formasPagamento ON tbl_rot_LancCaixa.CodFormaPag_LancCaixa = tbl_cad_formasPagamento.Cod_formasPagamento
WHERE (((tbl_rot_LancCaixa.usuariologado_lanccaixa)=[Formulários]![frm_saldocaixa_individual]![CxUsuario]) AND ((tbl_rot_LancCaixa.DtMovimento_LancCaixa)=[Formulários]![frm_saldocaixa_individual]![txt_data]));
