SELECT Sum(Nz([ValorPagoPagCli_CR],0)) AS ValorPago, Sum(Nz([JurosPagCli_CR],0)) AS Juros, Sum(Nz([DescontoPagCli_CR],0)) AS Desconto, Sum(Nz((([ValorPagoPagCli_CR]+[JurosPagCli_CR])-[DescontoPagCli_CR]),0)) AS TotalValorPago, tbl_rot_PagCli_contasReceber.DataPag_ContasReceberDET, tbl_cad_formasPagamento.Nome_FormaPagamento, tbl_rot_PagCli_contasReceber.DescricaoPagCli_CR
FROM ((tbl_rot_ContasReceber_det LEFT JOIN tbl_rot_PagCli_contasReceber ON tbl_rot_ContasReceber_det.cod_ContasReceberDET = tbl_rot_PagCli_contasReceber.codContasReceberDET) LEFT JOIN tbl_cad_formasPagamento ON tbl_rot_PagCli_contasReceber.FormaPagPagCli_CR = tbl_cad_formasPagamento.Cod_formasPagamento) LEFT JOIN tbl_cad_UsuarioSistema ON tbl_rot_PagCli_contasReceber.MatFunc = tbl_cad_UsuarioSistema.matFuncionario
GROUP BY tbl_rot_PagCli_contasReceber.DataPag_ContasReceberDET, tbl_cad_formasPagamento.Nome_FormaPagamento, tbl_rot_PagCli_contasReceber.DescricaoPagCli_CR, tbl_cad_UsuarioSistema.login_usuarioSistema
HAVING (((tbl_rot_PagCli_contasReceber.DataPag_ContasReceberDET)=[Formulários]![frm_saldocaixa_individual]![txt_data]) AND ((tbl_cad_UsuarioSistema.login_usuarioSistema) Like "*" & [Formulários]![frm_saldocaixa_individual]![CxUsuario] & "*"));
