SELECT tbl_rot_vendasDetalhadas.Cod_venda, tbl_cad_prodserv.Cod_ProdServ, tbl_rot_vendasDetalhadas.Quantidade_VendasDet, tbl_NrSerie_Prod.NumeroSerieProd, tbl_VendasDET_NRSerie.LiberacaoEstoque, tbl_rot_vendas.Grupo_vendas, tbl_rot_vendasDetalhadas.QtdInt_Prodserv, tbl_rot_vendasDetalhadas.QutLiberada_Estoque, [Quantidade_VendasDet]-[Qutliberada_estoque] AS [Qtd à liberar]
FROM (((tbl_rot_vendasDetalhadas LEFT JOIN tbl_cad_prodserv ON tbl_rot_vendasDetalhadas.Cod_produto = tbl_cad_prodserv.Cod_ProdServ) LEFT JOIN tbl_VendasDET_NRSerie ON tbl_rot_vendasDetalhadas.Cod_VendasDet = tbl_VendasDET_NRSerie.CodVendasDET_CVNS) LEFT JOIN tbl_NrSerie_Prod ON tbl_VendasDET_NRSerie.Cod_NrSerie_CVNS = tbl_NrSerie_Prod.cod_NrSerie) LEFT JOIN tbl_rot_vendas ON tbl_rot_vendasDetalhadas.Cod_venda = tbl_rot_vendas.Cod_venda
WHERE (((tbl_rot_vendas.Grupo_vendas)=[Formulários]![Frm_rot_vendas]![Vendas]![Grupo_vendas] Or (tbl_rot_vendas.Grupo_vendas)=4) AND ((tbl_cad_prodserv.Grupo_prodServ)="produto"));
